void m(int x) {
    if (x > 0) {
        int a = 11;
        System.out.println(a);
    } else {
        int a = 12;
        System.out.println(a);
    }
}

void m2(int x) {
    if (x <= 0) {
        int a = 11;
        System.out.println(a);
    } else {
        int b = 12;
        System.out.println(b);
    }
}
